INSERT INTO suttas VALUES(1,'dn1','/sutta-pitaka/digha-nikaya/silakkhandha-vagga',1,'DN 1',NULL,'pli',NULL,NULL,'Brahmajālasutta',NULL,NULL,replace('1. Paribbājakakathā\n\nEvaṁ me sutaṁ— ekaṁ samayaṁ bhagavā antarā ca rājagahaṁ antarā ca nāḷandaṁ addhānamaggappaṭipanno hoti mahatā bhikkhusaṅghena saddhiṁ pañcamattehi bhikkhusatehi. Suppiyopi kho paribbājako antarā ca rājagahaṁ antarā ca nāḷandaṁ addhānamaggappaṭipanno hoti saddhiṁ antevāsinā brahmadattena māṇavena. Tatra sudaṁ suppiyo paribbājako anekapariyāyena buddhassa avaṇṇaṁ bhāsati, dhammassa avaṇṇaṁ bhāsati, saṅghassa avaṇṇaṁ bhāsati; suppiyassa pana paribbājakassa antevāsī brahmadatto māṇavo anekapariyāyena buddhassa vaṇṇaṁ bhāsati, dhammassa vaṇṇaṁ bhāsati, saṅghassa vaṇṇaṁ bhāsati. Itiha te ubho ācariyantevāsī aññamaññassa ujuvipaccanīkavādā bhagavantaṁ piṭṭhito piṭṭhito anubandhā honti bhikkhusaṅghañca. Variant: anubandhā → anubaddhā (bj, pts1ed)\n\nAtha kho bhagavā ambalaṭṭhikāyaṁ rājāgārake ekarattivāsaṁ upagacchi saddhiṁ bhikkhusaṅghena. Variant: upagacchi → upagañchi (bj, sya-all, km, pts1ed)Suppiyopi kho paribbājako ambalaṭṭhikāyaṁ rājāgārake ekarattivāsaṁ upagacchi antevāsinā brahmadattena māṇavena. Tatrapi sudaṁ suppiyo paribbājako anekapariyāyena buddhassa avaṇṇaṁ bhāsati, dhammassa avaṇṇaṁ bhāsati, saṅghassa avaṇṇaṁ bhāsati; suppiyassa pana paribbājakassa antevāsī brahmadatto māṇavo anekapariyāyena buddhassa vaṇṇaṁ bhāsati, dhammassa vaṇṇaṁ bhāsati, saṅghassa vaṇṇaṁ bhāsati. Itiha te ubho ācariyantevāsī aññamaññassa ujuvipaccanīkavādā viharanti.','\n',char(10)),NULL,NULL,NULL,NULL,NULL,NULL,'2021-06-09 15:14:39',NULL);
INSERT INTO suttas VALUES(2,'dn2','/sutta-pitaka/digha-nikaya/silakkhandha-vagga',2,'DN 2',NULL,'pli',NULL,NULL,'Sāmaññaphalasutta',NULL,NULL,replace('1. Rājāmaccakathā\n\nEvaṁ me sutaṁ—ekaṁ samayaṁ bhagavā rājagahe viharati jīvakassa komārabhaccassa ambavane mahatā bhikkhusaṅghena saddhiṁ aḍḍhateḷasehi bhikkhusatehi.\n\nTena kho pana samayena rājā māgadho ajātasattu vedehiputto tadahuposathe pannarase komudiyā cātumāsiniyā puṇṇāya puṇṇamāya rattiyā rājāmaccaparivuto uparipāsādavaragato nisinno hoti.\n\nAtha kho rājā māgadho ajātasattu vedehiputto tadahuposathe udānaṁ udānesi: “ramaṇīyā vata bho dosinā ratti, abhirūpā vata bho dosinā ratti, dassanīyā vata bho dosinā ratti, pāsādikā vata bho dosinā ratti, lakkhaññā vata bho dosinā ratti. Kaṁ nu khvajja samaṇaṁ vā brāhmaṇaṁ vā payirupāseyyāma, yaṁ no payirupāsato cittaṁ pasīdeyyā”ti? ','\n',char(10)),NULL,NULL,NULL,NULL,NULL,NULL,'2021-06-09 15:16:08',NULL);
INSERT INTO suttas VALUES(3,'mn1','/sutta-pitaka/majjhima-nikaya/mula-pannasa/mulapariyaya-vagga',1,'MN 1',NULL,'pli',NULL,NULL,'Mūlapariyāyasutta',NULL,NULL,replace('Evaṁ me sutaṁ— ekaṁ samayaṁ bhagavā ukkaṭṭhāyaṁ viharati subhagavane sālarājamūle. Tatra kho bhagavā bhikkhū āmantesi: “bhikkhavo”ti.\n\n“Bhadante”ti te bhikkhū bhagavato paccassosuṁ. Bhagavā etadavoca:\n\n“sabbadhammamūlapariyāyaṁ vo, bhikkhave, desessāmi. Taṁ suṇātha, sādhukaṁ manasi karotha, bhāsissāmī”ti.\n\n“Evaṁ, bhante”ti kho te bhikkhū bhagavato paccassosuṁ. Bhagavā etadavoca:\n\n“Idha, bhikkhave, assutavā puthujjano ariyānaṁ adassāvī ariyadhammassa akovido ariyadhamme avinīto, sappurisānaṁ adassāvī sappurisadhammassa akovido sappurisadhamme avinīto— pathaviṁ pathavito sañjānāti; Variant: pathaviṁ → paṭhaviṁ (bj, sya-all, km, pts1ed)pathaviṁ pathavito saññatvā pathaviṁ maññati, pathaviyā maññati, pathavito maññati, pathaviṁ meti maññati, pathaviṁ abhinandati. Taṁ kissa hetu? ‘Apariññātaṁ tassā’ti vadāmi.','\n',char(10)),NULL,NULL,NULL,NULL,NULL,NULL,'2021-06-10 17:19:33',NULL);
INSERT INTO suttas VALUES(4,'mn2','/sutta-pitaka/majjhima-nikaya/mula-pannasa/mulapariyaya-vagga',2,'MN 2',NULL,'pli',NULL,NULL,'Sabbāsavasutta',NULL,NULL,replace('Evaṁ me sutaṁ— ekaṁ samayaṁ bhagavā sāvatthiyaṁ viharati jetavane anāthapiṇḍikassa ārāme. Tatra kho bhagavā bhikkhū āmantesi: “bhikkhavo”ti.\n\n“Bhadante”ti te bhikkhū bhagavato paccassosuṁ. Bhagavā etadavoca:\n\n“sabbāsavasaṁvarapariyāyaṁ vo, bhikkhave, desessāmi. Taṁ suṇātha, sādhukaṁ manasi karotha, bhāsissāmī”ti.\n\n“Evaṁ, bhante”ti kho te bhikkhū bhagavato paccassosuṁ. Bhagavā etadavoca:\n\n“Jānato ahaṁ, bhikkhave, passato āsavānaṁ khayaṁ vadāmi, no ajānato no apassato. Kiñca, bhikkhave, jānato kiñca passato āsavānaṁ khayaṁ vadāmi? Yoniso ca manasikāraṁ ayoniso ca manasikāraṁ. Ayoniso, bhikkhave, manasikaroto anuppannā ceva āsavā uppajjanti, uppannā ca āsavā pavaḍḍhanti; yoniso ca kho, bhikkhave, manasikaroto anuppannā ceva āsavā na uppajjanti, uppannā ca āsavā pahīyanti.','\n',char(10)),NULL,NULL,NULL,NULL,NULL,NULL,'2021-06-10 17:20:03',NULL);
